import React from 'react'
import { saveAs } from "file-saver";
import { useState } from 'react';
import { useEffect } from 'react';


function RandomImage(props) {

  const [val,setVal] = useState();


//   const alttext = props.name + " image";  
let imgsrc = ''; 
let imgsrc2 = '';  

let imgsrc3 = '';  

    useEffect(()=>{
         imgsrc = `https://source.unsplash.com/720x1080/?${props.name}`;
         imgsrc2 = `https://source.unsplash.com/1020x1080/?${props.name}`;
         imgsrc3 = `https://source.unsplash.com/900x1260/?${props.name}`;

        setVal(imgsrc);
        console.log(val);
    },[props.name])

    const downloadFunction = ()=>{

        saveAs(
            val,
            "example.jpg"
          );

    }     



  return (
        <>

          <button onClick={downloadFunction}>{imgsrc}</button> 
           <img src={imgsrc} alt='image' className='randomimage1' />
           <img src={imgsrc2} alt='image2' className='randomimage2' />
           <img src={imgsrc3} alt='image3' className='randomimage3' />

        </>
  )
}

export default RandomImage;
