import React, { useState } from 'react'

const MyAccordian = (props) =>{

    const [show, setShow] = useState(false);
    console.log(props.id);
    return(
          <>
          <div className='accordian_question_section'>
                <h3 id={props.id} onClick={() =>{                   

                  let demo =document.getElementsByClassName(props.id);
                  setShow(!show)
                  if((document.querySelector(`id`)) !== demo){
                        setShow(show)    
                  } 

            }}
                ><i>{show?"-":"+"}</i>
                   <span> {props.question} </span></h3>



                {show && <p className={props.id} >{props.answer}</p>} 
          </div>


          </>  
    )           
}

export default MyAccordian
