import React, { useState } from 'react'

const MyAccordian = (props) =>{

    const [show, setShow] = useState({id:'',status:false});

    return(
          <>
          <div className='accordian_question_section'>
                <h3 onClick={() => setShow({id:props.id,status:!show.status})}><i>{show.status?"-":"+"}</i>
                   <span id={props.id}> {props.question} </span></h3>
                {show.status && show.status === props.id && <p className='answer_div'>{props.answer}</p>} 
          </div>


          </>  
    )           
}

export default MyAccordian
