import React, { useState } from 'react'



const MyAccordian = (props) =>{

    const [show, setShow] = useState(false);
    const [answer,setAnwer] = useState(false);

    return(
          <>
          <div className='accordian_question_section'>
                <h3 onClick={() => {setShow(!show)
               let demo = document.querySelector('.answer_div')
                demo.classList.add(myClass);
                if(demo.classList.contains(myClass)){
                  demo.classList.remove(myClass);
                  setAnwer(true);
                }   


            }}><i>{show?"-":"+"}</i>
             <span> {props.question} </span></h3>

              {answer  &&  <p className='answer_div'>{props.answer}</p>  } 

          </div> 

          </>  
    )           
}

export default MyAccordian
